import {
    ADD_PRODUCT,
    SUCCESFULL_ADD_PRODUCT,
    FAILED_ADD_PRODUCT
} from '../types';

// Crear nuevos productos
export function newProductAction(product) {
    return (dispatch) => {
        dispatch(addProduct());
        try {
            dispatch(succesfullAddProduct(product));
        } catch (error) {
            dispatch(failedAddProduct(true));
        }
    }
}

const addProduct = () => ({
    type: ADD_PRODUCT
})
