import {
    SHOW_ALERT,
    HIDE_ALERT
} from '../types';

export function showAlert(alert) {
    return (dispatch) => {
        dispatch(showErrorAlert(alert))
    }
}

const showErrorAlert = alert => ({
    type: SHOW_ALERT,
    payload: alert
})
